import React, { Component } from 'react';
import Header from './HeaderComponent.js';
import Footer from './FooterComponent.js';
import UploadItem from './UploadItemComponent';
import { Switch, Route, Redirect, withRouter } from 'react-router-dom';

let username = '';
class Main extends Component {
  componentDidMount() {}

  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className='App'>
        <Header />
        <Switch location={this.props.location}>
          <Route
            exact
            path='/home'
            component={() => (
              <Home
                products={this.props.products.products.filter(
                  product => product.approved
                )}
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />
          <Route
            exact
            path='/search'
            component={() => (
              <Search
                products={this.props.products.products.filter(
                  product => product.approved
                )}
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
              />
            )}
          />
          <Route
            exact
            path='/canteens'
            component={() => (
              <Shops
                products={this.props.canteens.canteens.filter(
                  product => product.cat === 'Books' && product.approved
                )}
                title='Books'
                productsLoading={this.props.canteens.isLoading}
                productsErrMess={this.props.canteens.errMess}
                user={this.props.auth}
              />
            )}
          />
          <Route
            exact
            path='/canteens'
            component={() => (
              <Shops
                products={this.props.canteens.canteens.filter(
                  product => product.cat === 'Books' && product.approved
                )}
                title='Books'
                productsLoading={this.props.canteens.isLoading}
                productsErrMess={this.props.canteens.errMess}
                user={this.props.auth}
              />
            )}
          />
          <Route
            exact
            path='/books'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product => product.cat === 'Books' && product.approved
                )}
                title='Books'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />
          <PrivateRouteAdmin
            exact
            path='/pending'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product => !product.approved
                )}
                title='Pending Products'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />

          <PrivateRoute
            exact
            path='/uploads'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product => product.owner._id === this.props.auth.userinfo._id
                )}
                title='My Uploads'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />

          <Route
            exact
            path='/stationary'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product => product.cat === 'Stationary' && product.approved
                )}
                title='Stationary'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />

          <Route
            exact
            path='/chatbot'
            component={() => <ChatFull postText={this.props.postText} />}
          />

          <Route
            exact
            path='/electronics'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product =>
                    product.cat === 'Electronic Gadgets' && product.approved
                )}
                title='Electronic Gadgets'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />

          <Route
            exact
            path='/bicycles'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product => product.cat === 'Bicycles' && product.approved
                )}
                title='Bicycles'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />

          <Route
            exact
            path='/clothes'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product => product.cat === 'Clothes' && product.approved
                )}
                title='Clothes'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />

          <Route
            exact
            path='/sports'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product => product.cat === 'Sports' && product.approved
                )}
                title='Sports'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />

          <Route
            exact
            path='/others'
            component={() => (
              <Products
                products={this.props.products.products.filter(
                  product => product.cat === 'Others' && product.approved
                )}
                title='Others'
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
                user={this.props.auth}
                toggleEditModal={this.toggleEditModal}
                toggleDeleteModal={this.toggleDeleteModal}
                changeSelected={this.changeSelected}
                favorites={
                  this.props.auth.isAuthenticated
                    ? this.props.favorites.favorites
                    : false
                }
                postFavorite={this.props.postFavorite}
              />
            )}
          />

          <Route exact path='/products/:productId' component={ProductWithId} />
          <Route path='/products/:productId/owner' component={OwnerProduct} />
          <PrivateRouteCommon
            exact
            path='/profile'
            component={() => (
              <Profile
                auth={this.props.auth}
                editUser={this.props.editUser}
                editPassword={this.props.editPassword}
              />
            )}
          />
          <PrivateRouteCommon
            exact
            path='/wallet'
            component={() => <Wallet auth={this.props.auth} />}
          />
          <PrivateRoute
            exact
            path='/upload_product'
            component={() => (
              <UploadItem
                isAdmin={
                  this.props.auth.userinfo == null
                    ? false
                    : this.props.auth.userinfo.admin
                }
                postProduct={this.props.postProduct}
                products={this.props.products.products}
                productsLoading={this.props.products.isLoading}
                productsErrMess={this.props.products.errMess}
              />
            )}
          />
          <PrivateRoute
            exact
            path='/bid_history/:productId'
            component={HistoryWithId}
          />
          {/*             <PrivateRouteAdmin exact path='/logs' component={() => <Log
                      bids={this.props.bids}
                     />}
                      />
                         <PrivateRouteAdmin exact path='/list_students' component={() => <UserList
                      users={this.props.users.users.filter((user)=>(!user.admin))}
                      usersLoading={this.props.users.isLoading}
                      usersErrMess={this.props.users.errMess}
                     />}
                      />
                         <PrivateRouteAdmin exact path='/list_admins' component={() => <UserList
                      users={this.props.users.users.filter((user)=>(user.admin))}
                      usersLoading={this.props.users.isLoading}
                      usersErrMess={this.props.users.errMess}
                     />}
                      />
                       <PrivateRouteAdmin exact path='/bid' component={() => <Bid
                      auth={this.props.auth}
                      products={this.props.products.products}
                      productsLoading={this.props.products.isLoading}
                      productsErrMess={this.props.products.errMess}
                      users={this.props.users.users}
                      usersLoading={this.props.users.isLoading}
                      usersErrMess={this.props.users.errMess}
                      postBid={this.props.postBid}
                       />} />
                      <PrivateRouteAdmin exact path='/return' component={() => <Return
                      bids={this.props.bids}
                      auth={this.props.auth}
                      freezeBid={this.props.freezeBid}
                      />} />*/}
          <PrivateRouteCommon
            exact
            path='/favorites'
            component={() => (
              <Favorites
                favorites={this.props.favorites}
                deleteFavorite={this.props.deleteFavorite}
                fetchFavorites={this.props.fetchFavorites}
              />
            )}
          />
          <Route path='/users/:userId' component={UserWithId} />
          {/*<PrivateRouteAdmin path='/stats' component={() => <Stats
                      bids={this.props.bids}
                      products={this.props.products.products}
                      productsLoading={this.props.products.isLoading}
                      productsErrMess={this.props.products.errMess}
                      users={this.props.users.users}
                      usersLoading={this.props.users.isLoading}
                      usersErrMess={this.props.users.errMess}
                      />}/>*/}
          <Redirect to='/home' />
        </Switch>
        <Footer />
        <Modal
          isOpen={this.state.isDeleteModalOpen}
          toggle={this.toggleDeleteModal}
        >
          <ModalHeader toggle={this.toggleDeleteModal}>
            Confirm Deletion
          </ModalHeader>
          <ModalBody>
            Product details : <br />
            <br />
            Name :{' '}
            {this.state.selectedProduct
              ? this.state.selectedProduct.name
              : ''}{' '}
            <br />
            Category :{' '}
            {this.state.selectedProduct
              ? this.state.selectedProduct.cat
              : ''}{' '}
            <br />
            {this.state.selectedProduct
              ? this.state.selectedProduct.bid
                ? 'Bidding Range : ' +
                  this.state.selectedProduct.price +
                  ' - ' +
                  this.state.selectedProduct.max_bid
                : 'Price : ' + this.state.selectedProduct.price
              : ''}{' '}
            <br /> <br />
            Are you sure you wish to delete this product ? <br />
            <br />
            <Button
              color='danger'
              onClick={() => {
                this.props.deleteProduct(this.state.selectedProduct._id);
                this.toggleDeleteModal();
              }}
            >
              Yes
            </Button>{' '}
            <Button
              color='warning'
              onClick={() => {
                this.toggleDeleteModal();
              }}
            >
              No
            </Button>
          </ModalBody>
        </Modal>
        {this.state.selectedProduct ? (
          <Modal
            isOpen={this.state.isEditModalOpen}
            toggle={this.toggleEditModal}
          >
            <ModalHeader toggle={this.toggleEditModal}>
              Edit a product
            </ModalHeader>
            <ModalBody>
              <LocalForm onSubmit={values => this.handleSubmitEdit(values)}>
                <Row className='form-group'>
                  <Label htmlFor='name' md={2}>
                    Name{' '}
                  </Label>
                  <Col md={10}>
                    <Control.text
                      model='.name'
                      id='name'
                      name='name'
                      defaultValue={this.state.selectedProduct.name}
                      className='form-control'
                      validators={{
                        required,
                        minLength: minLength(3),
                        uniqueName: uniqueName(this.state.selectedProduct.name)
                      }}
                    />
                    <Errors
                      className='text-danger'
                      model='.name'
                      show='touched'
                      messages={{
                        required: 'Required',
                        minLength: 'Must be greater than 2 characters',
                        uniqueName:
                          ' There exists a product with this name already'
                      }}
                    />
                  </Col>
                </Row>
                <Row className='form-group'>
                  <Col md={4}>
                    <Control.checkbox
                      model='.bid'
                      id='bid'
                      name='bid'
                      className='form-control'
                      disabled={false}
                      checked={this.state.bid}
                      defaultChecked={this.state.bid}
                      onChange={e => {
                        this.setState({ bid: !this.state.bid });
                      }}
                    />
                  </Col>
                  <Col md={8}>
                    <Label check htmlFor='bid'>
                      {' '}
                      Allow Bidding
                    </Label>
                  </Col>
                </Row>
                {this.state.bid ? (
                  <React.Fragment>
                    <Row className='form-group'>
                      <Label htmlFor='price' md={4}>
                        Minimum Price (in &#8377;){' '}
                      </Label>
                      <Col md={8}>
                        <Control.text
                          model='.price'
                          id='price'
                          name='price'
                          defaultValue={this.state.selectedProduct.price}
                          className='form-control'
                          validators={{
                            requiredNum,
                            minVal: minVal(10),
                            isNumber
                          }}
                        />
                        <Errors
                          className='text-danger'
                          model='.price'
                          show='touched'
                          messages={{
                            requiredNum: ' Required',
                            minVal: ' Must be greater than 10 Rs.',
                            isNumber: ' Must be a number'
                          }}
                        />
                      </Col>
                    </Row>
                    <Row className='form-group'>
                      <Label htmlFor='max_bid' md={4}>
                        Maximum Price (in &#8377;){' '}
                      </Label>
                      <Col md={8}>
                        <Control.text
                          model='.max_bid'
                          id='max_bid'
                          name='max_bid'
                          defaultValue={this.state.selectedProduct.max_bid}
                          className='form-control'
                          validators={{
                            requiredNum,
                            maxVal: maxVal(100000),
                            isNumber
                          }}
                        />
                        <Errors
                          className='text-danger'
                          model='.max_bid'
                          show='touched'
                          messages={{
                            requiredNum: ' Required',
                            maxVal: ' Must be less than 100000 Rs.',
                            isNumber: ' Must be a number'
                          }}
                        />
                      </Col>
                    </Row>
                    <Row className='form-group'>
                      <Label htmlFor='incr' md={4}>
                        Minimum Increment (in &#8377;){' '}
                      </Label>
                      <Col md={8}>
                        <Control.text
                          model='.incr'
                          id='incr'
                          name='incr'
                          defaultValue={this.state.selectedProduct.incr}
                          className='form-control'
                          validators={{
                            requiredNum,
                            minVal: minVal(10),
                            maxVal: maxVal(10000),
                            multiple: multiple(10),
                            isNumber
                          }}
                        />
                        <Errors
                          className='text-danger'
                          model='.incr'
                          show='touched'
                          messages={{
                            requiredNum: ' Required',
                            minVal: ' Must be greater than 10 Rs.',
                            maxVal: ' Must be less than 10000 Rs.',
                            multiple: ' Must be a multiple of 10',
                            isNumber: ' Must be a number'
                          }}
                        />
                      </Col>
                    </Row>
                  </React.Fragment>
                ) : (
                  <Row className='form-group'>
                    <Label htmlFor='price' md={4}>
                      Price (in &#8377;){' '}
                    </Label>
                    <Col md={8}>
                      <Control.text
                        model='.price'
                        id='price'
                        name='price'
                        defaultValue={this.state.selectedProduct.price}
                        className='form-control'
                        validators={{
                          requiredNum,
                          minVal: minVal(10),
                          isNumber
                        }}
                      />
                      <Errors
                        className='text-danger'
                        model='.price'
                        show='touched'
                        messages={{
                          requiredNum: ' Required',
                          minVal: ' Must be greater than 10 Rs.',
                          isNumber: ' Must be a number'
                        }}
                      />
                    </Col>
                  </Row>
                )}

                <Row className='form-group'>
                  <Col>
                    <Label htmlFor='cat'>Category</Label>
                    <Control.select
                      model='.cat'
                      id='cat'
                      className='form-control'
                      defaultValue={this.state.selectedProduct.cat}
                    >
                      <option>Stationary</option>{' '}
                      <option>Electronic Gadgets</option>
                      <option>Bicycles</option> <option>Clothes</option>
                      <option>Sports</option> <option>Books</option>
                      <option>Others</option>
                    </Control.select>
                  </Col>
                </Row>

                <Row className='form-group'>
                  <Label htmlFor='description' md={2}>
                    Description
                  </Label>
                  <Col md={10}>
                    <Control.textarea
                      model='.description'
                      id='description'
                      name='description'
                      rows='12'
                      defaultValue={this.state.selectedProduct.description}
                      className='form-control'
                    />
                  </Col>
                </Row>
                <Row>
                  <Col className='ml-auto mr-auto'>
                    <Button type='submit' className='bg-primary'>
                      Submit
                    </Button>
                  </Col>
                </Row>
              </LocalForm>
            </ModalBody>
          </Modal>
        ) : (
          <React.Fragment />
        )}
      </div>
    );
  }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Main));
